        $h5pEditorBranchingScenarioParams = ['name' => "H5PEditor.BranchingScenario", "major_version" => 1, "minor_version" => 4];
        $h5pEditorBranchingScenarioLib = DB::table('h5p_libraries')->where($h5pEditorBranchingScenarioParams)->first();
