            DB::table('h5p_libraries')->where($h5pEditorBranchingScenarioParams)->update([
                'preloaded_js' => 'dist/dist.js',
                'preloaded_css' => '',
            ]);

            // insert libraries languages
            $this->insertLibrariesLanguages($h5pEditorBranchingScenarioLib->id);
        }
