<?php

use Illuminate\Database\Seeder;

class H5PAddBrightcoveLibrarySeeder extends Seeder
{
    /**
     * Run the database seeds.
     *
     * @return void
     */
    public function run()
    {
        $h5pBrightcoveIVLibParams = ['name' => "H5P.BrightcoveInteractiveVideo", "major_version" => 1, "minor_version" => 1];
        $h5pBrightcoveIVLib = DB::table('h5p_libraries')->where($h5pBrightcoveIVLibParams)->first();

      if (empty($h5pBrightcoveIVLib)) {
          $h5pBrightcoveIVLibId = DB::table('h5p_libraries')->insertGetId([
                          'name' => 'H5P.BrightcoveInteractiveVideo',
                          'title' => 'Brightcove Interactive Video',
                          'major_version' => 1,
                          'minor_version' => 1,
                          'patch_version' => 0,
                          'embed_types' => 'div',
                          'runnable' => 1,
                          'restricted' => 0,
                          'fullscreen' => 1,
                          'preloaded_js' => 'dist/h5p-interactive-video.js',
                          'preloaded_css' => 'dist/h5p-interactive-video.css',
                          'drop_library_css' => '',
                          'semantics' => $this->getSemantics(),
                          'tutorial_url' => ' ',
                          'has_icon' => 1
          ]);

          // insert dependent libraries
          $this->insertDependentLibraries($h5pBrightcoveIVLibId);

          // insert libraries languages
          $this->insertLibrariesLanguages($h5pBrightcoveIVLibId);
      }
    
    }

    /**
     * Insert Dependent Libraries
     * @param $h5pBrightcoveIVLibId
     */
    private function insertDependentLibraries($h5pBrightcoveIVLibId)
    {
        //Preloaded Dependencies
        $h5pFontAwesomeParams = ['name' => "FontAwesome", "major_version" => 4, "minor_version" => 5];
        $h5pFontAwesomeLib = DB::table('h5p_libraries')->where($h5pFontAwesomeParams)->first();
        $h5pFontAwesomeLibId = $h5pFontAwesomeLib->id;

        $h5pJQueryUIParams = ['name' => "jQuery.ui", "major_version" => 1, "minor_version" => 10];
        $h5pJQueryUILib = DB::table('h5p_libraries')->where($h5pJQueryUIParams)->first();
        $h5pJQueryUILibId = $h5pJQueryUILib->id;

        $h5pVideoParams = ['name' => "H5P.Video", "major_version" => 1, "minor_version" => 6];
        $h5pVideoLib = DB::table('h5p_libraries')->where($h5pVideoParams)->first();
        $h5pVideoLibId = $h5pVideoLib->id;

        $h5pDragNBarParams = ['name' => "H5P.DragNBar", "major_version" => 1, "minor_version" => 5];
        $h5pDragNBarLib = DB::table('h5p_libraries')->where($h5pDragNBarParams)->first();
        $h5pDragNBarLibId = $h5pDragNBarLib->id;


        // Editor Dependencies
        $h5pEditorWizardParams = ['name' => "H5PEditor.Wizard", "major_version" => 1, "minor_version" => 2];
        $h5pEditorWizardLib = DB::table('h5p_libraries')->where($h5pEditorWizardParams)->first();
        $h5pEditorWizardLibId = $h5pEditorWizardLib->id;

        $h5pEditorBrightcoveInteractiveVideoParams = ['name' => "H5PEditor.BrightcoveInteractiveVideo", "major_version" => 1, "minor_version" => 1];
        $h5pEditorBrightcoveInteractiveVideoLib = DB::table('h5p_libraries')->where($h5pEditorBrightcoveInteractiveVideoParams)->first();
        if(empty($h5pEditorBrightcoveInteractiveVideoLib)) {
            $h5pEditorBrightcoveInteractiveVideoLibId = DB::table('h5p_libraries')->insertGetId([
                'name' => 'H5PEditor.BrightcoveInteractiveVideo',
                'title' => 'Brightcove Interactive Video Editor',
                'major_version' => 1,
                'minor_version' => 1,
                'patch_version' => 0,
                'embed_types' => '',
                'runnable' => 0,
                'restricted' => 0,
                'fullscreen' => 0,
                'preloaded_js' => 'Scripts/image-radio-button-group.js,Scripts/interactive-video-editor.js,Scripts/guided-tours.js,Scripts/require-completion.js',
                'preloaded_css' => 'styles/image-radio-button-group.css,styles/interactive-video-editor.css,styles/require-completion.css',
                'drop_library_css' => '',
                'semantics' => '',
                'tutorial_url' => ' ',
                'has_icon' => 0
            ]);
            $this->insertEditorDependentLibraries($h5pEditorBrightcoveInteractiveVideoLibId);


        } else {
            $h5pEditorBrightcoveInteractiveVideoLibId = $h5pEditorBrightcoveInteractiveVideoLib->id;
        }

        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pBrightcoveIVLibId,
            'required_library_id' => $h5pFontAwesomeLibId,
            'dependency_type' => 'preloaded'
        ]);
        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pBrightcoveIVLibId,
            'required_library_id' => $h5pJQueryUILibId,
            'dependency_type' => 'preloaded'
        ]);
        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pBrightcoveIVLibId,
            'required_library_id' => $h5pVideoLibId,
            'dependency_type' => 'preloaded'
        ]);

        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pBrightcoveIVLibId,
            'required_library_id' => $h5pDragNBarLibId,
            'dependency_type' => 'preloaded'
        ]);

        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pBrightcoveIVLibId,
            'required_library_id' => $h5pEditorWizardLibId,
            'dependency_type' => 'editor'
        ]);
        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pBrightcoveIVLibId,
            'required_library_id' => $h5pEditorBrightcoveInteractiveVideoLibId,
            'dependency_type' => 'editor'
        ]);

    }

