<?php

use Illuminate\Database\Seeder;

class H5PAddTrueFalseLibrarySeeder extends Seeder
{
    /**
     * Run the database seeds.
     *
     * @return void
     */
    public function run()
    {
        $h5pTrueFalseLibParams = ['name' => "H5P.TrueFalse", "major_version" => 1, "minor_version" => 8];
        $h5pTrueFalseLib = DB::table('h5p_libraries')->where($h5pTrueFalseLibParams)->first();

      if (empty($h5pTrueFalseLib)) {
          $h5pTrueFalseLibId = DB::table('h5p_libraries')->insertGetId([
                          'name' => 'H5P.TrueFalse',
                          'title' => 'True/False Question',
                          'major_version' => 1,
                          'minor_version' => 8,
                          'patch_version' => 0,
                          'embed_types' => 'iframe',
                          'runnable' => 1,
                          'restricted' => 0,
                          'fullscreen' => 0,
                          'preloaded_js' => 'scripts/h5p-true-false.js,scripts/h5p-true-false-answer-group.js,scripts/h5p-true-false-answer.js',
                          'preloaded_css' => 'styles/h5p-true-false.css',
                          'drop_library_css' => '',
                          'semantics' => $this->getSemantics(),
                          'tutorial_url' => ' ',
                          'has_icon' => 1
          ]);

          // insert dependent libraries
          $this->insertDependentLibraries($h5pTrueFalseLibId);

          // insert libraries languages
          $this->insertLibrariesLanguages($h5pTrueFalseLibId);
      }
    
    }

    /**
     * Insert Dependent Libraries
     * @param $h5pTrueFalseLibId
     */
    private function insertDependentLibraries($h5pTrueFalseLibId)
    {
        //Preloaded Dependencies
        $h5pFontAwesomeParams = ['name' => "FontAwesome", "major_version" => 4, "minor_version" => 5];
        $h5pFontAwesomeLib = DB::table('h5p_libraries')->where($h5pFontAwesomeParams)->first();
        $h5pFontAwesomeLibId = $h5pFontAwesomeLib->id;


        $h5pQuestionParams = ['name' => "H5P.Question", "major_version" => 1, "minor_version" => 5];
        $h5pQuestionLib = DB::table('h5p_libraries')->where($h5pQuestionParams)->first();
        $h5pQuestionLibId = $h5pQuestionLib->id;

        $h5pJoubelUIParams = ['name' => "H5P.JoubelUI", "major_version" => 1, "minor_version" => 3];
        $h5pJoubelUILib = DB::table('h5p_libraries')->where($h5pJoubelUIParams)->first();
        $h5pJoubelUILibId = $h5pJoubelUILib->id;

        $h5pFontIconsParams = ['name' => "H5P.FontIcons", "major_version" => 1, "minor_version" => 0];
        $h5pFontIconsLib = DB::table('h5p_libraries')->where($h5pFontIconsParams)->first();
        $h5pFontIconsLibId = $h5pFontIconsLib->id;


        // Editor Dependencies
        $h5pEditorRadioGroupParams = ['name' => "H5PEditor.RadioGroup", "major_version" => 1, "minor_version" => 1];
        $h5pEditorRadioGroupLib = DB::table('h5p_libraries')->where($h5pEditorRadioGroupParams)->first();
        $h5pEditorRadioGroupLibId = $h5pEditorRadioGroupLib->id;

        $h5pEditorShowWhenParams = ['name' => "H5PEditor.ShowWhen", "major_version" => 1, "minor_version" => 0];
        $h5pEditorShowWhenLib = DB::table('h5p_libraries')->where($h5pEditorShowWhenParams)->first();
        $h5pEditorShowWhenLibId = $h5pEditorShowWhenLib->id;


        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pTrueFalseLibId,
            'required_library_id' => $h5pFontAwesomeLibId,
            'dependency_type' => 'preloaded'
        ]);
        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pTrueFalseLibId,
            'required_library_id' => $h5pQuestionLibId,
            'dependency_type' => 'preloaded'
        ]);
        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pTrueFalseLibId,
            'required_library_id' => $h5pJoubelUILibId,
            'dependency_type' => 'preloaded'
        ]);
        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pTrueFalseLibId,
            'required_library_id' => $h5pFontIconsLibId,
            'dependency_type' => 'preloaded'
        ]);


        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pTrueFalseLibId,
            'required_library_id' => $h5pEditorRadioGroupLibId,
            'dependency_type' => 'editor'
        ]);

        DB::table('h5p_libraries_libraries')->insert([
            'library_id' => $h5pTrueFalseLibId,
            'required_library_id' => $h5pEditorShowWhenLibId,
            'dependency_type' => 'editor'
        ]);

    }

