     * Run the database seeds.
     *
     * @return void
     */
    public function run()
    {
        $h5pCIVLibParams = ['name' => "H5P.CurrikiInteractiveVideo", "major_version" => 1, "minor_version" => 0];
        $h5pCIVLibLib = DB::table('h5p_libraries')->where($h5pCIVLibParams)->first();
        if (empty($h5pCIVLibLib)) {          
          	$h5pCIVLibId = DB::table('h5p_libraries')->insertGetId([
                          'name' => 'H5P.CurrikiInteractiveVideo',
                          'title' => 'Curriki Interactive Video',
                          'major_version' => 1,
                          'minor_version' => 0,
                          'patch_version' => 0,
                          'embed_types' => 'iframe',
                          'runnable' => 1,
                          'restricted' => 0,
                          'fullscreen' => 1,
                          'preloaded_js' => 'dist/h5p-interactive-video.js',
                          'preloaded_css' => 'dist/h5p-interactive-video.css',
                          'semantics' => $this->getCIVSemantics(),
                          'tutorial_url' => '',
                          'has_icon' => 1
          	]);
        }else {
            $h5pCIVLibId = $h5pCIVLibLib->id;
        }    
        // insert dependent libraries
        $this->insertDependentLibraries($h5pCIVLibId);
        // insert libraries languages
        $this->insertLibrariesLanguages($h5pCIVLibId);
    }

